--Genera la script per eliminare tutte le tabelle, viste, funzioni, procedure, sequenzeSELECT 'drop table '||TNAME||' cascade constraints PURGE;' FROM TABWHERE TABTYPE = 'TABLE'AND TNAME not like 'BIN$%'UNIONSELECT 'purge table  "'||TNAME||'" ;' FROM TABWHERE TABTYPE = 'TABLE'AND TNAME like 'BIN$%'UNIONSELECT 'drop view '||TNAME||' ;' FROM TABWHERE TABTYPE = 'VIEW'UNIONSELECT DISTINCT 'drop '||TYPE||' '||NAME||';' FROM USER_SOURCEWHERE TYPE = 'FUNCTION' OR TYPE = 'PROCEDURE'UNIONSELECT 'DROP SEQUENCE '||SEQUENCE_NAME||';' FROM SEQUNIONSELECT 'call DBMS_SCHEDULER.DROP_JOB(''' || JOB_NAME || ''');'  FROM USER_SCHEDULER_JOBSUNIONSELECT 'call DBMS_SCHEDULER.DROP_PROGRAM(''' || PROGRAM_NAME || ''');'  FROM USER_SCHEDULER_PROGRAMSUNIONSELECT 'call DBMS_SCHEDULER.DROP_SCHEDULE(''' || SCHEDULE_NAME || ''');'  FROM user_scheduler_schedulesUNIONSELECT 'DROP SYNONYM '|| SYNONYM_NAME || '; ' FROM USER_SYNONYMSUNIONSELECT 'DROP PACKAGE '||OBJECT_NAME||';' FROM USER_OBJECTSWHERE OBJECT_TYPE = 'PACKAGE';